import {Injectable} from "@angular/core";
import {HttpClient, HttpHeaders} from "@angular/common/http";
import { EMPTY, Observable, firstValueFrom } from "rxjs";



@Injectable()
export class PDVService {

    constructor(private http: HttpClient) {

    }

    addPushSubscriber(sub:any) {
        console.log('sub service', sub);
        return this.http.post<Array<Notification>>('https://mocktarget.apigee.net/echo', [{
          "notification": {
            "title": "Crédito Pessoal",
            "body": "teste",
            "vibrate": [100, 50, 100],
            "data": {
              "dateOfArrival": "2023-08-02",
              "primaryKey": 1
            }
          }
        }]);
    }

    async dados() {
      return await firstValueFrom(EMPTY, {
        defaultValue: [{
          "notification": {
              "title": "Crédito Pessoal",
              "body": "Cliente elegível no caixa U000012!!",
              "vibrate": [100, 50, 100],
              "data": {
                  "dateOfArrival": "2023-08-02",
                  "primaryKey": 1
              },
              "actions": [{
                  "action": "explore",
                  "title": "Ofertar ao cliente"
              }]
            }
          }]});
    }
}
