import { NgModule, isDevMode } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';

import { AppComponent } from './app.component';
import { ServiceWorkerModule, SwPush, SwRegistrationOptions, SwUpdate } from '@angular/service-worker';
import { PDVService } from './services/pdv.service';
import { HttpClientModule } from '@angular/common/http';

@NgModule({
  declarations: [
    AppComponent
  ],
  imports: [
    BrowserModule,
    HttpClientModule,
    ServiceWorkerModule.register('ngsw-worker.js', {
      enabled: !isDevMode(),
      // Register the ServiceWorker as soon as the application is stable
      // or after 30 seconds (whichever comes first).
      registrationStrategy: 'registerWhenStable:30000'
    })
  ],
  providers: [
    PDVService,
    {
      provide: SwRegistrationOptions,
      useFactory: () => ({enabled: location.search.includes('sw=true')}),
    }
  ],
  bootstrap: [AppComponent]
})
export class AppModule {
  readonly VAPID_PUBLIC_KEY = "BLjKEyVxw9XVPfhrX1HwuTOeHTcnOf5BacH6w7k7_LTTVnqX6A9S6OOpE27pZTNbcB_mrIRP73WvG_P-8aPRx0A";

  constructor(
    private swPush:SwPush,
    private update:SwUpdate,
    private pdvService: PDVService,
    private svcWorker: ServiceWorkerRegistration){
    update.versionUpdates.subscribe(update =>{
        console.log("Nova versão disponível");
      });

    this.SubscribeToPush();
    swPush.messages.subscribe(msg =>{
        console.log('msg', JSON.stringify(msg));
    });
    this.subscribeToNotificationClicks();
  }
  private subscribeToNotificationClicks() {
    // #docregion subscribe-to-notification-clicks
    this.swPush.notificationClicks.subscribe(
        ({action, notification}) => {
            // TODO: Do something in response to notification click.
            console.log('action', action);
            console.log('notification', notification);
        });
    // #enddocregion subscribe-to-notification-clicks
  }
  SubscribeToPush(){
    navigator.serviceWorker.ready.then(registration=>{
      this.pdvService.addPushSubscriber(null).subscribe(result => {
        result.forEach(item=>{
          registration.showNotification(item.title, {
            body: item.body,
            icon: item.icon,
            vibrate: [50,100,50]
          });
        });
      });
    });
